    val file = File(filePath)
    if (!file.exists()) {
        throw IllegalArgumentException("File does not exist: $filePath")
    }
    return file.readLines()
}

fun readLinesFromFile(filePath: String): List<String> {
    return File(filePath).useLines { it.toList() }
}

fun readLinesFromFile(filePath: String): List<String> {
    val file = java.io.File(filePath)
    if (!file.exists()) {
        throw IllegalArgumentException("File does not exist: $filePath")
    }
    return file.readLines()
}

import java.io.File

fun readFirstLineOfFile(filePath: String): String? {
    return File(filePath).useLines { lines -> lines.firstOrNull() }
}

fun readTextFile(filePath: String): String {
    val file = java.io.File(filePath)
    return if (file.exists() && file.isFile) {
        file.readText()
    } else {
        throw IllegalArgumentException("File not found or is not a regular file: $filePath")
    }
}

fun readFileLines(filePath: String): List<String> {
    return File(filePath).useLines { it.toList() }
}

import java.io.File

fun readFirstLineOfFile(filePath: String): String? {
    return File(filePath).bufferedReader().use { it.readLine() }
}

import java.io.File

fun readLinesFromFile(filePath: String): List<String> {
    return File(filePath).useLines { it.toList() }
}

import java.io.File

fun readFirstLineFromFile(filePath: String): String? {
    return File(filePath).useLines { it.firstOrNull() }
}

import java.io.File

fun readFileAsLines(filePath: String): List<String> {
    return File(filePath).readLines()
}

fun readFileLines(filePath: String): List<String> {
    val file = java.io.File(filePath)
    return if (file.exists() && file.isFile) {
        file.readLines()
    } else {
        emptyList()
    }
}

import java.io.File

fun readFileContents(filePath: String): String {
    return File(filePath).bufferedReader().use { it.readText() }
}
