    """Counts the number of lines in a file."""
    line_count = 0
    try:
        with open(file_path, 'r') as file:
            for _ in file:
                line_count += 1
    except FileNotFoundError:
        raise ValueError(f"The file at {file_path} was not found.")
    return line_count
