def count_lines_in_file(file_path):
    """Counts the number of lines in a given file."""
    line_count = 0
    try:
        with open(file_path, 'r') as file:
            for _ in file:
                line_count += 1
    except FileNotFoundError:
        return "File not found."
    except IOError:
        return "Error reading file."
    return line_count
