use std::path::Path;

/// Reads lines from a file at the given path and returns them as a vector of strings.
fn read_lines_from_file(file_path: &str) -> io::Result<Vec<String>> {
    let path = Path::new(file_path);
    let file = File::open(&path)?;
    let buffered = io::BufReader::new(file);
    Ok(buffered.lines().collect::<Result<_, _>>()?)
}

