fn read_file_line_by_line(file_path: &str) -> io::Result<()> {
    if let Ok(lines) = read_lines(file_path) {
        for line in lines {
            if let Ok(content) = line {
                println!("{}", content);
            }
        }
    }
    Ok(())
}

