/// Reads a file line-by-line and collects lines containing a specified keyword.
fn collect_lines_with_keyword<P: AsRef<Path>>(file_path: P, keyword: &str) -> io::Result<Vec<String>> {
    let file = File::open(file_path)?;
    let reader = io::BufReader::new(file);

    let lines_with_keyword: Vec<String> = reader.lines()
        .filter_map(|line| line.ok())
        .filter(|line| line.contains(keyword))
        .collect();

    Ok(lines_with_keyword)
}

