fn read_lines_with_prefix(file_path: &str, prefix: &str) -> Result<Vec<String>> {
    let file = OpenOptions::new().read(true).open(file_path)?;
    let reader = BufReader::new(file);
    let mut lines_with_prefix = Vec::new();

    for line in reader.lines() {
        let line = line?;
        if line.starts_with(prefix) {
            lines_with_prefix.push(line);
        }
    }

    Ok(lines_with_prefix)
}

