    guard let fileURL = URL(string: path) else {
        return nil
    }

    do {
        let contents = try String(contentsOf: fileURL, encoding: .utf8)
        return contents
    } catch {
        print("Error reading file at \(path): \(error.localizedDescription)")
        return nil
    }
}
