import Foundation

func writeTextToFile(text: String, to fileName: String, in directory: FileManager.SearchPathDirectory = .documentDirectory) throws {
    let fileManager = FileManager.default
    let urls = fileManager.urls(for: directory, in: .userDomainMask)
    guard let directoryURL = urls.first else {
        throw NSError(domain: "DirectoryNotFoundError", code: 1, userInfo: nil)
    }

    let fileURL = directoryURL.appendingPathComponent(fileName)

    do {
        try text.write(to: fileURL, atomically: true, encoding: .utf8)
    } catch {
        throw NSError(domain: "FileWriteError", code: 2, userInfo: [NSLocalizedDescriptionKey: "Failed to write text to file: \(error.localizedDescription)"])
    }
}
