```swift
func readFileContents(atPath path: String) throws -> String {
    let fileManager = FileManager.default
    guard fileManager.fileExists(atPath: path) else {
        throw NSError(domain: "FileErrorDomain", code: 404, userInfo: [NSLocalizedDescriptionKey: "File not found"])
    }

    let fileURL = URL(fileURLWithPath: path)
    do {
        let contents = try String(contentsOf: fileURL, encoding: .utf8)
        return contents
    } catch {
        throw NSError(domain: "FileErrorDomain", code: 500, userInfo: [NSLocalizedDescriptionKey: "Failed to read file"])
    }
}
