import Foundation

func writeTextToFile(text: String, fileName: String, directory: FileManager.SearchPathDirectory = .documentDirectory) throws {
    let fileManager = FileManager.default
    let urls = fileManager.urls(for: directory, in: .userDomainMask)

    guard let documentDirectory = urls.first else {
        throw NSError(domain: "FileErrorDomain", code: 1, userInfo: [NSLocalizedDescriptionKey: "Unable to access document directory"])
    }

    let fileURL = documentDirectory.appendingPathComponent(fileName)
    try text.write(to: fileURL, atomically: true, encoding: .utf8)
}
