function findDuplicates(arr) {
    const duplicates = [];
    const seen = new Set();
    for (let i = 0; i < arr.length; i++) {
        if (seen.has(arr[i])) {
            if (!duplicates.includes(arr[i])) {
                duplicates.push(arr[i]);
            }
        } else {
            seen.add(arr[i]);
        }
    }
    return duplicates;
}

