```javascript
function permute(str) {
    const results = [];

    function swap(chars, i, j) {
        const temp = chars[i];
        chars[i] = chars[j];
        chars[j] = temp;
    }

    function generate(n, arr) {
        if (n === 1) {
            results.push(arr.join(''));
            return;
        }
        for (let i = 0; i < n; i++) {
            generate(n - 1, arr);
            swap(arr, n % 2 === 0 ? i : 0, n - 1);
        }
    }

    generate(str.length, str.split(''));
    return results;
}

const permutations = permute("abc");
console.log(permutations);
```

