    let mergedList = [];
    let i = 0, j = 0;

    for (; i < list1.length && j < list2.length;) {
        if (list1[i] < list2[j]) {
            mergedList.push(list1[i]);
            i++;
        } else {
            mergedList.push(list2[j]);
            j++;
        }
