require 'net/smtp'

class EmailService
  def initialize(smtp_server, port, domain, username, password)
    @smtp_server = smtp_server
    @port = port
    @domain = domain
    @username = username
    @password = password
  end

  def send_email(from, to, subject, body)
    message = <<~MESSAGE_END
      From: #{from}
      To: #{to}
      Subject: #{subject}

      #{body}
    MESSAGE_END

    Net::SMTP.start(@smtp_server, @port, @domain, @username, @password, :plain) do |smtp|
      smtp.send_message message, from, to
    end
  end
end

# Example usage:
# email_service = EmailService.new('smtp.example.com', 587, 'example.com', 'user', 'pass')
# email_service.send_email('from@example.com', 'to@example.com', 'Subject', 'Email body')
