require 'tk'
require 'time'

class DigitalClock
  def initialize
    @root = TkRoot.new { title "Digital Clock" }
    @time_label = TkLabel.new(@root) do
      font TkFont.new('times 50 bold')
      pack { padx 15; pady 15; side 'top' }
    end
    update_time
    Tk.mainloop
  end

  def update_time
    @time_label.text = Time.now.strftime("%H:%M:%S")
    Tk.after(1000) { update_time }
  end
end

