require 'redis'
require 'redis-namespace'

class Cache
  def initialize
    @redis = Redis.new
    @namespace = Redis::Namespace.new(:cache, redis: @redis)
  end

  def fetch(key)
    @namespace.get(key)
  end

  def store(key, value, ttl = 3600)
    @namespace.set(key, value)
    @namespace.expire(key, ttl)
  end
end

# Example usage
cache = Cache.new
cache.store('key', 'value')
puts cache.fetch('key')
