require 'rgeo'
require 'rgeo/geo_json'

# Sample geospatial data (GeoJSON format)
geojson_data = '{
  "type": "FeatureCollection",
  "features": [
    {
      "type": "Feature",
      "geometry": {
        "type": "Point",
        "coordinates": [-104.99404, 39.75621]
      },
      "properties": {
        "name": "Sample Location"
      }
    }
  ]
}'

# Parse the GeoJSON data
factory = RGeo::Geographic.spherical_factory(srid: 4326)
geojson_parser = RGeo::GeoJSON::Coder.new(factory: factory)
features = geojson_parser.decode(geojson_data, json_parser: :json)

# Visualize the data (using a simple terminal output here)
features.each do |feature|
  geom = feature.geometry
  puts "Feature: #{feature.properties['name']}, Coordinates: #{geom.coordinates}"
end
